pw.test("Status code is 400", () => {
  pw.expect(pw.response.status).toBe(400);
});

pw.test("Response says empty body", () => {
  pw.expect(pw.response.body).toContain("Empty body");
});

pw.test("Status code is 405", () => {
  pw.expect(pw.response.status).toBe(405);
});

pw.test("Response says method not allowed", () => {
  pw.expect(pw.response.body).toContain("Method Not Allowed");
});
